import React, { Component } from 'react';
import gql from 'graphql-tag';
import Form from './styles/Form';
import { FormField, TextInputField, Autocomplete, TextInput, Checkbox, Button, Heading, Combobox } from 'evergreen-ui';
import styled from 'styled-components';

const Styles = styled.div`
    background-color: ${props => props.theme.lightgrey};
    padding: 10rem 20rem 30rem 20rem;
`;
class DonationForm extends Component {
    state = {
        email: '',
        updates: false,
        gift: 0,
        allocation: ""
    };
    saveToState = e => {
        this.setState({ [e.target.name]: e.target.value });
    };

    render() {
        return (
            <Styles>
                <FormField>
                    <Heading size={500} marginTop="default">Email *</Heading>
                    <TextInputField
                        isInvalid={false}
                        inputWidth={400}
                    />
                    <Heading size={500} marginTop="default">Gift Amount *</Heading>
                    <Autocomplete height={50} width={400}
                        onChange={changedItem => console.log(changedItem)}
                        items={['$25', '$50', '$100', '$200', '$500']}
                    >
                        {(props) => {
                            const { getInputProps, getRef, inputValue, openMenu } = props
                            return (
                                <TextInput
                                    placeholder="Choose or Enter an Amount"
                                    value={inputValue}
                                    innerRef={getRef}
                                    label="Gift Amount"
                                    {...getInputProps({
                                        onFocus: () => {
                                            openMenu()
                                        }
                                    })}
                                />
                            )
                        }}
                    </Autocomplete>
                    <Heading size={500} marginTop="default">Allocation</Heading>
                    <Combobox
                        openOnFocus
                        width={400}
                        items={['Banana', 'Orange', 'Apple', 'Mango']}
                        onChange={selected => console.log(selected)}
                        placeholder="Select a Cat"
                    />

                    <div style={{ display: "flex", alignItems: "center" }}><Checkbox
                        onChange={e => setState({ checked: e.target.checked })}
                    /> <Heading size={500} marginTop={0} marginLeft={10} >Keep me updated on Free Spirits news</Heading> </div>
                    <Button height={50} marginTop={30} appearance="primary" intent="success" >
                        Complete Transaction
                </Button>


                </FormField>
            </Styles>
        );
    }
}

export default DonationForm;
