import React, { Component } from 'react';
import { Query } from 'react-apollo';
import gql from 'graphql-tag';
import styled from 'styled-components';
import Link from 'next/link'


const SINGLE_CAT_QUERY = gql`
    query SINGLE_CATS_QUERY($name: String!){
        cat(name: $name) {
            id
            name
            description
            habitat
            iucnStatus
            number
            subspecies
            lifeSpan
            size
            weight
            diet
            bg
            bg2
            photoSet {
            id
            url
            }
            articleSet {
            id
            title
            content
            }
        }
    }
`;

const Style = styled.div`
    .gallary {
        width: 100%;
        img {
            width: 100%;
            height: auto;
        }
    }
    .fact {

    }
`;

class Cat extends Component {
    render() {
        return (
            <Query query={SINGLE_CAT_QUERY}
                variables={{ name: this.props.query.name }}
            >
                {({ data, error, loading }) => {
                    if (loading) return <p>Loading...</p>;
                    if (error) return <p>Error: {error.message}</p>;
                    const cat = data.cat

                    return (
                        // id, name, description, habitat, iucnStatus, number, subspecies, lifeSpan, size, weight, diet, bg, bg2, photoSet, articleSet
                        <Style>
                            <div className='gallary'>
                                <img src={cat.bg} />
                            </div >
                            <div className='fact'>
                                <legend>{cat.name}</legend>

                                <table>

                                    <thead>
                                        <tr>
                                            <th>
                                                CAT FACTS
                                            </th>
                                            <th />
                                            <th />
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td />
                                            <td>IUCN RED LIST STATUS</td>
                                            <td>{cat.iucnStatus}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>LEFT IN THE WILD</td>
                                            <td>{cat.number}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>RANGE</td>
                                            <td>{cat.habitat}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>SUB-SPECIES</td>
                                            <td>{cat.subspecies}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>LIFE SPAN</td>
                                            <td>{cat.lifespan}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>SIZE</td>
                                            <td>{cat.size}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>WEIGHT</td>
                                            <td>{cat.weight}</td>
                                        </tr>
                                        <tr>
                                            <td />
                                            <td>DIET</td>
                                            <td>{cat.diet}</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </Style>
                    )
                }}
            </Query>
        );
    }
}

export default Cat;
