import React, { Component } from 'react';
import gql from 'graphql-tag';
import Form from './styles/Form';
import { FormField, TextInputField, Autocomplete, TextInput, Checkbox, Button, Heading } from 'evergreen-ui';
import styled from 'styled-components';

const Styles = styled.div`
    background-color: ${props => props.theme.lightgrey};
    padding: 10rem 20rem 30rem 20rem;
`;
class DonationForm extends Component {
    state = {
        email: '',
        updates: false,
        gift: 0,
        allocation: ""
    };
    saveToState = e => {
        this.setState({ [e.target.name]: e.target.value });
    };

    render() {
        return (
            <Styles>
                <FormField>
                    <Heading size={500} marginTop="default">Email *</Heading>
                    <TextInputField
                        isInvalid={false}
                        inputWidth={400}
                    />
                    <Heading size={500} marginTop="default"><Checkbox
                        label="Keep me updated on Free Spirits news"
                        onChange={e => setState({ checked: e.target.checked })}
                    /> Keep me updated on Free Spirits news</Heading>
                    <Autocomplete height={50}
                        onChange={changedItem => console.log(changedItem)}
                        items={['$25', '$50', '$100', '$200', '$500']}
                    >
                        {(props) => {
                            const { getInputProps, getRef, inputValue, openMenu } = props
                            return (
                                <TextInput
                                    placeholder="Choose or Enter an Amount"
                                    value={inputValue}
                                    innerRef={getRef}
                                    label="Gift Amount"
                                    {...getInputProps({
                                        onFocus: () => {
                                            openMenu()
                                        }
                                    })}
                                />
                            )
                        }}
                    </Autocomplete>

                    <TextInputField
                        isInvalid={false}
                        required
                        label="Allocation"
                    />
                    <Button height={50} marginRight={16} appearance="primary" intent="success" >
                        Complete Transaction
                </Button>


                </FormField>
            </Styles>
        );
    }
}

export default DonationForm;
