import React, { Component } from 'react';
import { Query } from 'react-apollo';
import gql from 'graphql-tag';
import styled from 'styled-components';
import SlideShow from './SlideShow';
import { Button, TextInput, Autocomplete } from 'evergreen-ui'


const Center = styled.div`
  text-align: center;
`;


class Home extends Component {
  render() {
    return (
      <Center>
        <SlideShow />
        <Paragraph />
        <Donation />
        <div style={{ minHeight: "50vh" }} />
      </Center>
    );
  }
}

const ParagraphStyles = styled.div`
  display: grid;
  grid-template-columns: 300px auto;
  grid-column-gap: 5rem;
  padding: 10rem 20rem;
  background: ${props => props.theme.lightgrey};
  color: ${props => props.theme.black};

  div{
    text-align: left;
    padding: 2rem;
  }
`;

const Paragraph = (props) => (
  <ParagraphStyles>
    <div>
      <h1>Why Protect Big Cats?</h1>
    </div>
    <div>
      <p>Panthera is the only organization in the world devoted exclusively to the conservation of the worldâ€™s wild cats. Our team of leading biologists and law enforcement experts develop innovative strategies to address the dire threats facing cheetahs, jaguars, leopards, lions, pumas, snow leopards, and tigers.</p>
      <p>We are on the front lines, fighting to stop poaching, prevent conflict with people, conserve wild cat habitats, and reduce unsustainable legal hunting. These proven strategies don't just protect wild catsâ€”they also protect their vast landscapes and the endless variety of life within them. These wild places are crucial to our planetâ€™s healthâ€”and our own.</p>
      <Button
        appearance="primary"
        marginRight={16}
        intent="warning"
      >
        Read More
      </Button>
    </div>

  </ParagraphStyles>
)



const DonationStyles = styled.div`
  padding: 5rem 20rem;
  background: url("/static/image/donationbg.jpg");
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-size: cover;
  min-height: 50vh;
  color: white;

`;

const Donation = () => (
  <>
    <DonationStyles>
      <form>
        <Autocomplete height={30}
          onChange={changedItem => console.log(changedItem)}
          items={['$25', '$50', '$100', '$200', '$500']}
        >
          <h1>Donate</h1>

          {(props) => {
            const { getInputProps, getRef, inputValue, openMenu } = props
            return (
              <TextInput
                value={inputValue}
                innerRef={getRef}
                {...getInputProps({
                  onFocus: () => {
                    openMenu()
                  }
                })}
              />
            )
          }}
          <h1>
            to save the world's wild cats.
            </h1>

        </Autocomplete>

      </form>
    </DonationStyles>

  </>
)

export default Home;
