import Link from 'next/link';
import { Query } from 'react-apollo';
import Signout from './Signout';
import NavCat from './NavCat';
import styled from 'styled-components';
import gql from 'graphql-tag';

const NavStyles = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  justify-self: end;
  font-size: 2rem;
  a,
  button {
    padding: 1rem 3rem;
    display: flex;
    align-items: center;
    position: relative;
    font-size: 1em;
    background: none;
    cursor: pointer;
    color: white;
    @media (max-width: 700px) {
      font-size: 10px;
      padding: 0 10px;
    }
    &:after {
      height: 2px;
      background: white;
      content: '';
      width: 0;
      position: absolute;
      transform: translateX(-50%);
      transition: width 0.4s;
      transition-timing-function: cubic-bezier(1, -0.65, 0, 2);
      left: 50%;
      margin-top: 2rem;
    }
    &:hover,
    &:focus {
      outline: none;
      &:after {
        width: calc(100% - 60px);
      }
    }
  }

  @media (max-width: 1300px) {
    border-top: 1px solid white;
    width: 100%;
    justify-content: center;
    font-size: 1.5rem;
  }
`;

const CatList = styled.div`
`;

const ALL_CATS_QUERY = gql`
  query ALL_CATS_QUERY{
    allCats{
      id
      name
      image
      iucnStatus
    }
  }
`;



const Nav = () => (
  <>
    <NavStyles>
      <Link href="/aboutus">
        <a>About Us</a>
      </Link>
      <Link href="/cats">
        <a>Meet the Cats</a>
      </Link>
      <Link href="/donation">
        <a>Donation</a>
      </Link>
      <Link href="/adoption">
        <a>Adoption</a>
      </Link>
      <Link href="/signup">
        <a>Sign In</a>
      </Link>
    </NavStyles>
    <Query query={ALL_CATS_QUERY} >
      {({ data, error, loading }) => {
        if (loading) return <p>Loading...</p>;
        if (error) return <p>Error: {error.message}</p>;
        console.log(data);

        return (
          <CatList>{data.allCats.map(cat => <NavCat cat={cat} key={cat.id} />)}</CatList>

        );
      }}
    </Query>
  </>
);

export default Nav;
