import React, { Component } from 'react';
import { Query } from 'react-apollo';
import gql from 'graphql-tag';
import styled from 'styled-components';
import Link from 'next/link'


const SINGLE_CAT_QUERY = gql`
    query SINGLE_CATS_QUERY($name: String!){
        cat(name: $name) {
            id
            name
            description
            habitat
            iucnStatus
            number
            subspecies
            lifeSpan
            size
            weight
            diet
            bg
            bg2
            photoSet {
            id
            url
            }
            articleSet {
            id
            title
            content
            }
        }
    }
`;

const Gallary = styled.div`
    width: 100%;
    img {
        width: 100%;
        height: auto;
    }
  `;

class Cat extends Component {
    render() {
        return (
            <Query query={SINGLE_CAT_QUERY}
                variables={{ name: this.props.query.name }}
            >
                {({ data, error, loading }) => {
                    if (loading) return <p>Loading...</p>;
                    if (error) return <p>Error: {error.message}</p>;
                    const cat = data.cat
                    console.log(cat);

                    return (
                        <>
                            <Gallary>
                                <img src={cat.bg} />
                            </Gallary >
                            <Fact>
                                <table>
                                    <legend>{cat.name}</legend>

                                    <thead>
                                        <tr>
                                            <th>
                                                CAT FACTS
                                            </th>
                                            <th />
                                            <th />
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td />
                                            <td>IUCN RED LIST STATUS</td>
                                            <td>{cat}</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </Fact>
                        </>
                    )
                }}
            </Query>
        );
    }
}

export default Cat;
